import base64
import cv2
import numpy as np

# Replace this with your actual Base64 string
base64_string = ""

try:
    image_data = base64.b64decode(base64_string)  # Decode base64
    print(f"✅ Decoded Image Bytes Length: {len(image_data)} bytes")
    
    # Save for manual checking
    with open("debug_image.jpg", "wb") as f:
        f.write(image_data)
    print("✅ Image saved as debug_image.jpg")

    # Convert to NumPy array
    np_arr = np.frombuffer(image_data, np.uint8)
    image = cv2.imdecode(np_arr, cv2.IMREAD_COLOR)

    if image is None:
        print("❌ OpenCV failed to decode the image!")
    else:
        print("✅ OpenCV successfully decoded the image!")
        cv2.imshow("Debug Image", image)
        cv2.waitKey(0)
        cv2.destroyAllWindows()

except Exception as e:
    print(f"⚠️ Error: {e}")
